// src/components/Robot.js
import React, { useState } from 'react'; // Importez useState
import '../styles/Robot.scss'; // Importez le fichier SCSS
import robot from '../images/robot.webp'; // Assurez-vous que l'image est correctement importée

const Robot = () => {
    // État pour gérer l'affichage du message
    const [showMessage, setShowMessage] = useState(false);

    // Fonction pour gérer le clic sur le robot
    const handleClick = () => {
        setShowMessage(true);
        setTimeout(() => {
            setShowMessage(false); // Cache le message après 2 secondes
        }, 2000);
    };

    return (
        <div className="robot" onClick={handleClick}> {/* Ajoutez l'événement onClick */}
            <div className="bubble">
                <img src={robot} alt="Robot" />
            </div>
            {showMessage && <div className="message">Bienvenue</div>} {/* Affiche le message */}
        </div>
    );
};

export default Robot;
