import React, { useEffect, useState } from "react"; // Importez useEffect et useState
import "../styles/Navbar.scss";

const Navbar = () => {
  const [isTransparent, setIsTransparent] = useState(false); // État pour gérer la transparence

  // Fonction pour gérer le défilement
  const handleScroll = () => {
    const scrollTop = window.scrollY; // Position actuelle de défilement
    if (scrollTop > 50) {
      setIsTransparent(true); // Devenir transparent si le défilement est supérieur à 50px
    } else {
      setIsTransparent(false); // Revenir à normal si inférieur
    }
  };

  // Utilisez useEffect pour ajouter le gestionnaire d'événements de défilement
  useEffect(() => {
    window.addEventListener("scroll", handleScroll); // Ajoutez le gestionnaire
    return () => {
      window.removeEventListener("scroll", handleScroll); // Nettoyage
    };
  }, []);

  return (
    <nav className={`navbar ${isTransparent ? 'navbar-transparent' : ''}`}>
      <a className="nav-link" href="#about">À propos</a>
      <a className="nav-link" href="#Competences">Mes compétences</a>
      <a className="nav-link" href="#projects">Projets</a>
      <a className="nav-link" href="#contact">Contact</a>
    </nav>
  );
};

export default Navbar;
