// src/App.js
import React, { useEffect, useState } from "react"; // Importer useEffect et useState
import Header from "./components/Header";
import Navbar from "./components/Navbar";
import Footer from "./components/Footer";
import Home from "./pages/Home";
import About from "./pages/About";
import Projects from "./pages/Projects";
import Contact from "./pages/Contact";
import Loader from "./components/Loader"; // Importer le loader
import "./styles/Main.scss"; // Fichier de style principal

const App = () => {
  const [loading, setLoading] = useState(true); // État de chargement

  useEffect(() => {
    // Simule un temps de chargement
    const timer = setTimeout(() => {
      setLoading(false); // Met à jour l'état de chargement
    }, 2000); // Durée de 2 secondes

    return () => clearTimeout(timer); // Nettoyer l'effet
  }, []);

  return (
    <div>
      {loading ? ( // Afficher le loader si loading est true
        <Loader />
      ) : (
        <>
          <Header />
          <Navbar />

          <div className="main-container">
            <section id="home">
              <Home />
            </section>

            <section id="about">
              <About />
            </section>

            <section id="projects">
              <Projects />
            </section>

            <section id="contact">
              <Contact />
            </section>
          </div>

          <Footer />
        </>
      )}
    </div>
  );
};

export default App;
