// src/components/Contact.js
import React from 'react';
import '../styles/Contact.scss';

const Contact = () => {
    const emailAddress = "acalvet18@gmail.com"; // Remplacez par votre adresse e-mail
    const subject = "Contact via le site web"; // Sujet de l'e-mail
    const body = "Bonjour, je voudrais vous contacter au sujet de..."; // Corps de l'e-mail

    return (
        <div className="contact-container">
            <h2>Contactez-moi</h2>
            <a 
                href={`mailto:${emailAddress}?subject=${encodeURIComponent(subject)}&body=${encodeURIComponent(body)}`} 
                className="submit-button" // Utilisez le style du bouton pour le lien
            >
                Cliquez ici pour m'envoyer un e-mail
            </a>
        </div>
    );
};

export default Contact;
