import React, { useState, useEffect } from 'react';
import '../styles/Robot.scss';
import robot from '../images/robot.webp';

const Robot = () => {
    const [position, setPosition] = useState({ bottom: '80px', right: '20px' });
    const [showMessage, setShowMessage] = useState(false);

    // Définir les différentes positions possibles
    const positions = [
        { bottom: '80px', right: '20px' },
        { bottom: '150px', right: '100px' },
        { bottom: '200px', right: '50px' },
        { bottom: '100px', right: '200px' },
    ];

    // Fonction pour changer la position aléatoirement
    const changePosition = () => {
        const randomIndex = Math.floor(Math.random() * positions.length);
        setPosition(positions[randomIndex]);
    };

    // Gérer le clic sur le robot
    const handleClick = () => {
        alert("aie");
        setShowMessage(true); // Afficher le message
        changePosition(); // Changer la position
        setTimeout(() => setShowMessage(false), 2000); // Cacher le message après 2 secondes
    };

    return (
        <div className="robot" style={{ bottom: position.bottom, right: position.right }} onClick={handleClick}>
            <div className="bubble">
                <img src={robot} alt="Robot" />
            </div>
            {showMessage && (
                <div className="message">aie</div>
            )}
        </div>
    );
};

export default Robot;
