import { createGlobalStyle } from 'styled-components';

const GlobalStyle = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  body {
    font-family: 'Arial', sans-serif;
    background-color: #f4f4f4;
    color: #333;
  }

  h1, h2 {
    margin-bottom: 1rem;
    color: #333;
  }

  p {
    margin-bottom: 1rem;
    line-height: 1.6;
  }
`;

export default GlobalStyle;
